INSERT INTO `wp_posts` VALUES("252", "1", "2024-09-14 11:51:06", "2024-09-14 04:51:06", "<div class=\"box-detail-post top-post\">\r\n<h1 class=\"title-blogs-item\"><span style=\"color: #ff0000; font-family: \'times new roman\', times, serif;\"><strong>Giới thiệu nội thất</strong></span></h1>\r\n<div class=\"info_edu\">\r\n\r\n<span style=\"font-family: \'times new roman\', times, serif;\"><i class=\"fa fa-tags\"><i class=\"fa fa-calendar\" aria-hidden=\"true\"><img class=\"alignnone wp-image-54\" src=\"https://quynhvishop.shop/wp-content/uploads/2024/09/offer-300x300.png\" alt=\"\" width=\"19\" height=\"19\" /></i></i> Giới thiệu <i class=\"fa fa-tags\"><img class=\"alignnone wp-image-53\" src=\"https://quynhvishop.shop/wp-content/uploads/2024/09/calendar-300x300.png\" alt=\"\" width=\"19\" height=\"19\" /></i>15:37 - 07/01/2021</span>\r\n\r\n</div>\r\n</div>\r\n<div class=\"ckeditor-post\">\r\n\r\n<span style=\"font-family: \'times new roman\', times, serif;\">Công ty Cổ phần Xuân Hòa Việt Nam (Xuân Hòa) thành lập năm 1980 do Cộng Hòa Pháp tài trợ (qua thương hiệu của hãng xe Peugeot). Sau 40 năm, sản phẩm của Xuân Hòa đã trải rộng trên khắp cả nước và vươn ra quốc tế, với các đối tác lớn trong ngành nội thất như IKEA của Thụy Điển, Habitat của Pháp, Sankin của Nhật Bản. Bên cạnh đó, Nội thất Xuân Hòa còn là đối tác sản xuất khung ghế cho Toyota, mạ chi tiết cho Honda, đáp ứng tốt các yêu cầu cao về chất lượng và thời gian hoàn thành sản phẩm.</span>\r\n\r\n<span style=\"font-family: \'times new roman\', times, serif;\">Nội thất Xuân Hòa tự hào là nhà cung cấp sản phẩm cho các công trình trọng điểm quốc gia như Tòa nhà Quốc Hội, Trung tâm Hội nghị Quốc Gia, bệnh viện Bạch Mai… và các doanh nghiệp lớn trong nước như: Samsung Electronics Việt Nam, ngân hàng Phương Đông OCB, Tổng công ty Bưu điện Việt Nam VNPost, trường quốc tế Olympia…và nhiều công trình dân sinh khác.</span>\r\n\r\n<span style=\"font-family: \'times new roman\', times, serif;\">Bằng việc phát huy những điểm mạnh sẵn có trong 40 năm kinh nghiệm, đội ngũ nhân sự chất lượng cao kết hợp công nghệ và máy móc hiện đại, quy trình quản trị sản xuất tiên tiến cùng việc hợp tác với các đối tác quốc tế, Nội thất Xuân Hòa cam kết mang sản phẩm tiêu chuẩn chất lượng quốc tế phục vụ người tiêu dùng Việt.</span>\r\n\r\n<span style=\"font-family: \'times new roman\', times, serif;\">#bàn ghế xuân hòa</span>\r\n\r\n<span style=\"font-family: \'times new roman\', times, serif;\"><img class=\"aligncenter\" src=\"https://noithatxuanhoa.vn/uploads/tiny_uploads/gioi-thieu.jpg\" alt=\"\" width=\"600\" height=\"345\" /></span>\r\n\r\n</div>", "Giới thiệu", "", "inherit", "closed", "closed", "", "28-revision-v1", "", "", "2024-09-14 11:51:06", "2024-09-14 04:51:06", "", "28", "https://quynhvishop.shop/?p=252", "0", "revision", "", "0");
