INSERT INTO `wp_wc_admin_notes` VALUES("41", "woocommerce-WCSubscriptions-June-2023-updated-needed", "marketing", "en_US", "Action required: Security update of WooCommerce Subscriptions", "<strong>Your store requires a security update for the WooCommerce Subscriptions plugin</strong>. Please update the WooCommerce Subscriptions plugin immediately to address a potential vulnerability.", "{}", "pending", "woocommerce.com", "2024-09-01 13:00:52", NULL, "0", "plain", "", "0", "0", "info");
